import React, { useEffect } from "react";
import { Switch, Route, Router, Redirect } from "react-router-dom";
import Login from "./layout/auth/login";
import Register from "./layout/auth/register";

/* investors Route */
import InvestorsDashboard from "./layout/dashboard/investors/index";
import Dashboard from "./layout/dashboard/investors/main";
import Profile from "./layout/dashboard/investors/profile";
import InvestmentPlans from "./layout/dashboard/investors/plan";

import history from "./history";

function App() {
  useEffect(() => {
    if (
      localStorage.theme === "dark" ||
      (!("theme" in localStorage) &&
        window.matchMedia("(prefers-color-scheme: dark)").matches)
    ) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  });

  return (
    <div className="h-screen overflow-hidden flex items-center justify-center bg-gray-100 dark:bg-gray-800  dark:text-gray-200 select-none">
      <Router history={history}>
        <Switch>
          <Route path="/" component={Login} exact />
          <Route path="/register" component={Register} exact />

          {/* Invwstors Routes */}
          <InvestorsDashboard>
            <Redirect exact from={"/dashboard"} to={"/dashboard/main"} />
            <Route path="/dashboard/main" component={Dashboard} />
            <Route path="/dashboard/profile" component={Profile} />
            <Route path="/dashboard/plan" component={InvestmentPlans} />
          </InvestorsDashboard>

          <Route path="*"/>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
