import React from "react";
import { Field, reduxForm } from "redux-form";
import { renderFeild } from "./../../../../components/inputFeild";

const PaymentInfo = ({ prevPage, nexPage,handleSubmit }) => {
  const renderPayment = () => {
    return (
      <div className="mt-4">
        <div className="flex items-center mb-4">
          <p>Select a payment plan</p>{" "}
          <img
            src={require("../../../../assets/paystack.png").default}
            alt="aimart invest paystack picture"
            className=" ml-2 w-32"
          />
        </div>
        <select className="w-full px-3 py-2 mb-1 border-2 border-gray-200 rounded-md focus:outline-none focus:border-indigo-500 transition-colors">
          <option value="" disabled>
            --- select payment plan ---
          </option>
          <option value="true">Paystack</option>
          <option value="false">Bank payment</option>
        </select>
      </div>
    );
  };

  return (
    <div className="w-full max-w-3xl">
      <h1 className=" font-semibold text-3xl"> Payment Information</h1>

      <p className=" text-red-800 text-base italic">
        Please do not refresh this page
      </p>

      <p className="text-gray-800 text-base">step 5/7</p>

      <Field
        name="amount"
        component={renderFeild}
        disable="true"
        label="Amount used for investment"
      />

      <Field name="paymentPlan" component={renderPayment}/>
      <div className="flex items-center mt-2">
        <button
          className="bg-blue-800 text-white p-2 px-8 inline-flex rounded-lg"
          onClick={prevPage}
        >
          <svg
            className="w-6 h-6 "
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              stroke-width="2"
              d="M15 19l-7-7 7-7"
            ></path>
          </svg>
          Prev
        </button>

        <button
          className="bg-blue-800 text-white p-2 px-8 inline-flex ml-2 rounded-lg"
          onSubmit="submit"
        >
          Next
          <svg
            class="w-6 h-6"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              stroke-width="2"
              d="M9 5l7 7-7 7"
            ></path>
          </svg>
        </button>
      </div>
    </div>
  );
};

const validate = ({ paymentPlan }) => {
  const error = {};
  if (!paymentPlan) {
    error.paymentPlan = "select a payment plan";
  }
  return error;
};

export default reduxForm({
  form: "investmentForm",
  validate,
  destroyOnUnmount: false,
  forceUnregisterOnUnmount: true,
})(PaymentInfo);
