import React, { useEffect } from "react";
import { Switch, Route, Router } from "react-router-dom";
import Login from "./layout/auth/login";
import Regiter from "./layout/auth/register";

import Dashboard from "./layout/dashboard/investors/index";

function App() {
  useEffect(() => {
    if (
      localStorage.theme === "dark" ||
      (!("theme" in localStorage) &&
        window.matchMedia("(prefers-color-scheme: dark)").matches)
    ) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  });

  return (
    <div className="h-screen overflow-hidden flex items-center justify-center bg-gray-100 dark:bg-gray-800  dark:text-white">

    </div>
  );
}

export default App;
