import React from "react";
import { Field, reduxForm } from "redux-form";
import { renderFeild } from "./../../../../components/inputFeild";

const PaymentInfo = () => {
  const renderPayment = () => {
    return (
      <select>
        <option value="" disabled>--- select payment plan ---</option>
        <option value="true">Paystack</option>
        <option value="false">Bank payment</option>
      </select>
    );
  };

  return (
    <div className="w-full max-w-3xl">
      <h1 className=" font-semibold text-3xl"> Payment Information</h1>
      <img
        src={require("../../../../assets/paystack.png").default}
        alt="aimart invest paystack picture"
        className=" w-56"
      />

      <h2 className="text-2xl text-gray-800">Amount used for investment</h2>
      <Field name="amount" component={renderFeild} disabled="true" />
    </div>
  );
};

const validate = () => {};

export default reduxForm({
  form: "investmentForm",
  validate,
  destroyOnUnmount: false,
  forceUnregisterOnUnmount: true,
})(PaymentInfo);
