import React, { useState, useEffect } from "react";
import { plans } from "../../../../constant";
import { Field, reduxForm, SubmissionError } from "redux-form";
import { connect } from "react-redux";
import { parse } from "query-string";
import { renderFeild } from "../../../../components/inputFeild";



const renderOptionFeild = ()=>{
  return plans.map(plan=><select>
     <option value="" disabled>--Please choose a plan--</option>
     <option value="">{plan.duration.map()}</option>
  </select>)
}


const PlanDetails = ({ amount, handleSubmit, prevPage, nextPage, change }) => {
  const [invest, setinvest] = useState(null);
  const { id } = parse(window.location.search);

  const getInvestmentDetails = () => {
    return plans.find(
      (inv) =>
        inv.id === parseInt(id) && (
          <div>
            {change("plan", inv.name)}
            {change("duration", inv.duration)}
            {setinvest(inv)}
          </div>
        )
    );
  };


  const calculateROI = (amount) => {
    if (amount) {
      let percentROI = (invest.percentage / 100) * parseInt(amount);
      percentROI = percentROI + parseInt(amount);
      if (isNaN(percentROI)) return;
      return change(
        "roi",
        "₦" + percentROI.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")
      );
    }
  };

  useEffect(() => {
    getInvestmentDetails();
  }, [id]);

  const submit = (formValues) => {

     if(!formValues.amount) return
    console.log("amount", formValues.amount);
    const inrange = formValues.amount >= parseInt(invest.min) && formValues.amount <= parseInt(invest.max)
    if (!inrange) {
     throw new SubmissionError({
       amount:"invalid price range:kindly check amount range"
     })
    }

   nextPage()
    // if (!inrange) {
    //   throw new SubmissionError({
    //     amount: "Kindly choose within formValues.amount range",
    //     _error: 'Login failed!'
    //   });
    // }else{
    //   console.log('formValues',formValues);
    // }

  };

  return (
    <div className=" max-w-3xl w-full">
      <div>
        <h1 className="text-3xl font-semibold text-black">Plan Details</h1>
        <p className=" text-red-800 text-base italic">
          Please do not refresh this page
        </p>

        <p className="text-gray-800 text-base">step 1/4</p>

        <div className="my-4 ">
          <form onSubmit={handleSubmit(submit)}>
            <Field
              component={renderFeild}
              disable={true}
              name="plan"
              label="Plan Selected"
            />

            <Field
              component={renderFeild}
              disable={true}
              name="duration"
              label="Duration"
            />

            {/* <input
              onChange={(e) => {
                let percentROI = (invest.percentage / 100) * parseInt(e.target.value);
                percentROI = percentROI + parseInt(e.target.value);
                if(isNaN(percentROI)) return
                return change("roi", "₦"+percentROI.toString().replace(/\B(?=(\d{3})+(?!\d))/g, ","));
              }}
              type="text"
              className="w-full px-3 py-2 mb-1 border-2 border-gray-200 rounded-md focus:outline-none focus:border-indigo-500 transition-colors"
            /> */}

            <Field
              component={renderFeild}
              name="amount"
              label="Amount to invest"
              onBlur={() => calculateROI(amount)}
            />

            {invest && invest && (
              <p className="text-xs text-gray-800 mb-2">{`minimum investment is  ₦${invest.min
                .toString()
                .replace(
                  /\B(?=(\d{3})+(?!\d))/g,
                  ","
                )} maximum amount is  ₦${invest.max
                .toString()
                .replace(/\B(?=(\d{3})+(?!\d))/g, ",")}`}</p>
            )}

            <Field
              component={renderFeild}
              disable={true}
              name="roi"
              label="Your total ROI"
            />

            <div className="flex items-center">
              <button
                className="bg-blue-800 text-white p-2 px-8 inline-flex rounded-lg"
                onClick={prevPage}
              >
                <svg
                  className="w-6 h-6 "
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    stroke-linecap="round"
                    stroke-linejoin="round"
                    stroke-width="2"
                    d="M15 19l-7-7 7-7"
                  ></path>
                </svg>
                Prev
              </button>

              <button
                className="bg-blue-800 text-white p-2 px-8 inline-flex ml-2 rounded-lg"
                onClick={submit}
              >
                Next
                <svg
                  class="w-6 h-6"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    stroke-linecap="round"
                    stroke-linejoin="round"
                    stroke-width="2"
                    d="M9 5l7 7-7 7"
                  ></path>
                </svg>
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

const validate = ({ amount }) => {
  const error = {};

  if (!amount) {
    error.amount = "amount Feild Is Empty.";
  }

  return error;
};
const mapStateToProps = (state) => {
  let amount = state.form.investmentForm?.values.amount;
  return { amount };
};

export default connect(mapStateToProps)(
  reduxForm({
    form: "investmentForm",
    validate,
    destroyOnUnmount: false,
    forceUnregisterOnUnmount: true,
  })(PlanDetails)
);
