import React from "react";
import { Link } from "react-router-dom";
import { plans } from "../../../../constant"; // underline text-white group-hover:text-white

const Plans = () => {
  const renderPlans = () => {
    return plans.map((plan) => (
      <div>
        <div className="w-full p-4 md:w-1/2 lg:w-1/4 plan-card">
          <label className="flex flex-col rounded-lg shadow-lg group relative cursor-pointer hover:shadow-2xl">
            <div
              className={`w-full px-4 py-6 rounded-t-lg card-section-1 ${
                plan.name === "platinum excutive" && `bg-blue-500`
              }`}
            >
              <h3
                className={`mx-auto text-base font-semibold text-center underline ${
                  plan.name === "platinum executive" && "text-blue-500"
                } group-hover:text-white`}
              >
                {plan.name}
              </h3>
              <p
                className={`mt-2 text-5xl font-bold text-center group-hover:text-white ${
                  plan.name === "platinum executive"
                    ? "text-blue-500"
                    : "text-white"
                }`}
              >
                {plan.percentage}
              </p>
              <p
                className={`mt-2 text-base text-center uppercase group-hover:text-white ${
                  plan.name === "platinum executive"
                    ? "text-blue-500"
                    : "text-white"
                }`}
              >
                {plan.min}
              </p>
              <p
                className={`mt-2 text-base text-center uppercase group-hover:text-white ${
                  plan.name === "platinum executive"
                    ? "text-blue-500"
                    : "text-white"
                }`}
              >
                {plan.max}
              </p>
            </div>
            <div className="flex flex-col items-center justify-center w-full h-full py-6 rounded-b-lg bg-blue-500">
              <p className="text-xl text-white">{plan.duration}</p>
              <Link
                to="?plan='gold_basic'"
                className="w-5/6 py-2 mt-2 font-semibold text-center uppercase bg-white border border-transparent rounded text-blue-500"
              >
                Invest Here
              </Link>
            </div>
          </label>
        </div>
      </div>
    ));
  };

  return (
    <>
      <div className="container flex flex-wrap pt-4 pb-10 m-auto mt-6 md:mt-15 lg:px-12 xl:px-16">
        <div className="w-full px-0 lg:px-4">
          <h2 className="px-12 text-base font-bold text-center md:text-2xl text-blue-700">
            Choose your investment plan
          </h2>
          <p className="py-1 text-sm text-center text-blue-700 mb-10">
            Kindly click an investment plan to continue.
          </p>
          <div className="flex flex-wrap items-center justify-center py-4 pt-0">
            {re}
          </div>
        </div>
      </div>
    </>
  );
};

export default Plans;
