import { Subject } from "./subject";
import { productStatus } from "./product-status";
export interface productEventCreated {
    subject: Subject.PRODUCT_CREATED;
    data: {
        id: string;
        title: string;
        price: number;
        description: string;
        status: productStatus;
        owner: string;
        image: string;
        version: number;
    };
}
export interface productEventUpdated {
    subject: Subject.PRODUCT_UPDATED;
    data: {
        id: string;
        title: string;
        price: number;
        description: string;
        status: productStatus;
        owner: string;
        image: string;
        version: number;
    };
}
export interface productEventDeleted {
    subject: Subject.PRODUCT_DELETED;
    data: {
        id: string;
        owner: string;
        version: number;
    };
}
export interface profileEventCreated {
    subject: Subject.PROFILE_CREATED;
    data: {
        id: string;
        brandName: string;
        username: string;
        gsm: number;
        profilePic?: string;
        location: string;
        image: string;
        business?: [];
        version: number;
    };
}
export interface profileEventUpdate {
    subject: Subject.PROFILE_UPDATED;
    data: {
        id: string;
        brandName: string;
        username: string;
        gsm: number;
        profilePic?: string;
        location: string;
        image: string;
        business?: [];
        version: number;
    };
}
export interface orderCreatedEvent {
    subject: Subject.ORDER_CREATED;
    data: {
        id: string;
        version: number;
        userId: string;
        product: any;
        status: string;
        expiry: Date;
    };
}
export interface orderCancelledEvent {
    subject: Subject.ORDER_CANCELLED;
    data: {
        id: string;
        version: number;
        userId: string;
        status: string;
        product: any;
    };
}
export interface orderExpirationEvent {
    subject: Subject.EXPIRATION_COMPLETED;
    data: {
        orderId: string;
    };
}
export interface orderPaymentEvent {
    subject: Subject.PAYMENT_COMPLETED;
    data: {
        orderId: string;
        productId: string;
        title: string;
        price: number;
        productOwnerId: string;
        userId: string;
    };
}
